<style lang="scss" scoped>
.marks__cards {
    width: 100%;
    display: grid;
    grid-template-columns: repeat(2, 1fr);

    .marks__card {
        width: 100%;
        display: flex;
        column-gap: 50px;
        row-gap: 25px;
        align-items: center;
        padding: 50px;
        border: solid 1px #EBEBF0;
        justify-content: center;
        transition: .3s;

        &-desc {
            max-width: 360px;
            width: 100%;
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            row-gap: 15px;
        }

        &-title {
            font-size: calc(20px + 8 * (100vw / 1920));
            transition: .3s;

            &::first-letter {
                text-transform: uppercase;
            }
        }

        &-text {
            color: var(--text-color);

            &::first-letter {
                text-transform: uppercase;
            }
        }

        &-img {
            max-width: 140px;
            width: 100%;
            object-fit: cover;
            fill: var(--text-color);
            transition: .4s;
            display: flex;
            justify-content: center;
        }

        &:hover {
            border-color: var(--main-blue-color);

            .marks__card-title {
                color: var(--main-blue-color);
            }

            .marks__card-img {
                fill: #5C8EE6;
            }
        }
    }
}

@media (max-width: 1110px){
    .marks__cards {
        grid-template-columns: repeat(1, 1fr);
        justify-items: center;

        .marks__card {
            max-width: 600px;
        }
    }
}

@media (max-width: 576px){
    .marks__card {
        flex-direction: column;
        padding: 30px !important;
        
